mapper.select:

        SELECT 
	        sid studentId, 
	        sname studentName,
	        age age,
	        gender gender
        from student
        where 1=1


        	and sid=#{studentId,jdbcType=VARCHAR}




	        		and sname like #{studentNameLike,jdbcType=VARCHAR}




	        		and sname=#{studentName,jdbcType=VARCHAR}
